	private  static ArrayList&lt;Integer&gt; interesect(ArrayList&lt;Integer&gt; f, ArrayList&lt;Integer&gt; s) { 
		ArrayList&lt;Integer&gt; res = new ArrayList&lt;Integer&gt;();
		
		int i = 0, j = 0; 
		while (i != f.size() &amp;&amp; j != s.size()) { 
			
			if (f.get(i) &lt; s.get(j)) {
				i ++;
			} else if (f.get(i) &gt; s.get(j)) { 
				j ++;
			} else { 
				res.add(f.get(i)); 
				i ++;  j ++;
			}
		}
		
		
		return res; 
	}


