        sh = wb.getSheet(sheetName);
        Row row = sh.createRow(r);
        //Adding data column wise
        for (String h : data) {
            row.createCell(c++).setCellValue(h);
        }
        closeExcelInstance();
        FileOutputStream outputStream = new FileOutputStream(inFilePath);
        wb.write(outputStream);
        wb.close();
