    private fun setJsonValueRec(path: List&lt;String&gt;, index: Int, obj: JSONObject, value: Any?): JSONObject {
        return obj.put(
            path[index],
            when (index) {
                path.lastIndex -&gt; value
    
                else -&gt; setJsonValueRec(
                    path = path,
                    index = index + 1,
                    obj = obj.getJSONObject(path[index]),
                    value = value
                )
            }
