    public class CustomSessionRegistryImpl extends SessionRegistryImpl {
        private Set&lt;String&gt; disabledNextRefreshRequests = ConcurrentHashMap.newKeySet();
    
        public CustomSessionRegistryImpl() {
            super();
        }
    
        public void preventNextRefreshLastRequest(String sessionId) {
            Assert.hasText(sessionId, &quot;SessionId required as per interface contract&quot;);
            disabledNextRefreshRequests.add(sessionId);
        }
    
        @Override
        public void refreshLastRequest(String sessionId) {
            Assert.hasText(sessionId, &quot;SessionId required as per interface contract&quot;);
    
            SessionInformation info = getSessionInformation(sessionId);
    
