    @RequiredArgsConstructor
    public class CustomPreConcurrentSessionFilter extends GenericFilterBean {
        @Autowired
        private final SessionRegistry sessionRegistry;
    
        @Override
        public void doFilter(ServletRequest req, ServletResponse res, FilterChain chain)
                throws ServletException, IOException {
            HttpServletRequest request = (HttpServletRequest) req;
            HttpSession session = request.getSession(false);
            if (session != null) {
                SessionInformation info = sessionRegistry.getSessionInformation(session.getId());
                if (info != null &amp;&amp; !info.isExpired() &amp;&amp; sessionRegistry instanceof CustomSessionRegistryImpl) {
                    boolean shouldPrevent = URL_IGNORE_UPDATE_SESSION_LIFETIME.contains(request.getServletPath());
                    if (shouldPrevent) {
                        ((CustomSessionRegistryImpl) sessionRegistry).preventNextRefreshLastRequest(info.getSessionId());
                    }
                }
                if (info != null &amp;&amp; !info.isExpired()
                        &amp;&amp; (new Date().getTime() - info.getLastRequest().getTime())/1000 &gt;= session.getMaxInactiveInterval()
                ) {
                        //the session has timed out
                        info.expireNow();
                }
            }
            chain.doFilter(req, res);
        }
    }

