            }
        }
    
        public static void setViewBackgroundDrawable(@NonNull View view, @NonNull Context context, @DrawableRes int resId) {
            Drawable drawable = findDrawable(context, resId);
            if (drawable != null) {
                view.setBackground(drawable);
            }
        }
    }

