    String token = JWT.create()
                .withHeader(headers)
                .withAudience(environment.getProperty(&quot;audience&quot;))
                .withIssuer(environment.getProperty(&quot;developer_id&quot;))
                .withClaim(&quot;kid&quot;,environment.getProperty(&quot;key_id&quot;))
