    public double[] rgbToLab(int R, int G, int B) {

        double r, g, b, X, Y, Z, xr, yr, zr;

        // D65/2&#176;
        double Xr = 95.047;  
        double Yr = 100.0;
        double Zr = 108.883;
        

        // --------- RGB to XYZ ---------//
        
        r = R/255.0;
        g = G/255.0;
        b = B/255.0;
        
        if (r &gt; 0.04045)
            r = Math.pow((r+0.055)/1.055,2.4);
        else
            r = r/12.92;

        if (g &gt; 0.04045)
            g = Math.pow((g+0.055)/1.055,2.4);
        else
            g = g/12.92;

        if (b &gt; 0.04045)
            b = Math.pow((b+0.055)/1.055,2.4);
        else
