    public static &lt;T&gt; Iterable&lt;T&gt; concat(Iterable&lt;T&gt; x, Iterable&lt;T&gt; y)
    {
        for(T e: x)
    	{
    		yield return e;
    	}
    
        for(T e: y)
    	{
    		yield return e;
    	}
    }
    
