			int r = color.getRed();
			int g = color.getGreen();
			int b = color.getBlue();
			int maxRGB = Math.max(r, Math.max(g, b));
			int minRGB = Math.min(r, Math.min(g, b));
			int addition = maxRGB + minRGB;
