public class Tile {
    private final Pattern pattern; // The kind of Tile doesn&#39;t change.
    private final Position pos; // The pos doesn&#39;t change.
    private boolean visited; // Whether you entered the Tile can change.

    public Tile(Pattern pattern, Position pos) {
        this.pattern = pattern;
        this.pos = pos;
    }
    
    public Position getPos() {
