            startingAt[i] = helperSum;
        }
        
        //Searching optimal Y
        int sum = 0;
        for(int i = 0; i &lt; (n - 1); ++i) {
            sum = Math.max(sum, endingAt[i] + startingAt[i+2]);
        }

        return sum;

    }
