            int x = 1;
            int increment = 1;
            while (true) {
                if (System.getProperty(&quot;os.name&quot;).contains(&quot;Windows&quot;)) {
                    System.out.print(&quot;\u001b[H\u001b[2J\u001b[3J&quot;);
