        // either phone hanged up or ringing stopped
    }

or you can refer to this [solution][1]


Alternatively, You can also use [BroadcastReciever][2] to check the state change.

    public class PhoneStateReceiver extends BroadcastReceiver {
    
        @Override
        public void onReceive(Context context, Intent intent) {
            Bundle bundle = intent.getExtras();
    
            assert bundle != null;
            String phoneState = bundle.getString(TelephonyManager.EXTRA_STATE);
            Log.d(&quot;phoneCallReceiver&quot;, phoneState);
    
            if (intent.getStringExtra(TelephonyManager.EXTRA_STATE).equals(TelephonyManager.EXTRA_STATE_IDLE)) {
                Log.d(&quot;phoneCallReceiver&quot;, &quot;phone hanged up&quot;);
            }
            if (intent.getStringExtra(TelephonyManager.EXTRA_STATE).equals(TelephonyManager.EXTRA_STATE_OFFHOOK)) {
                Log.d(&quot;phoneCallReceiver&quot;, &quot;phone picked up&quot;);
            }
            if (phoneState.equals(TelephonyManager.EXTRA_STATE_RINGING)) {
                Log.d(&quot;phoneCallReceiver&quot;, &quot;phone ringing&quot;);
            }
        }
    }


  [1]: https://stackoverflow.com/a/10213843/16177121
  [2]: https://developer.android.com/reference/android/content/BroadcastReceiver

