    int[] seen = new int[seenSize];

    // iterate over the same array to create an array of any unique seen element
    int seenIndex = 0;
    for (int i = 0; i &lt; arr1.length; i++) {
      for (int j = 0; j &lt; arr1[i].length; j++) {
