void maxOccurence(String str) {
	char max_char = str.charAt(0),
	cur_char,
	prev = str.charAt(0);
	int cur_count = 0,
	max_count = 0,
	n;
	n = str.length();
	for (int i = 0; i &lt; n; i++) {
		cur_char = str.charAt(i);
		if (cur_char != prev) cur_count = 0;
		if (str.charAt(i) == cur_char) {
			cur_count++;
		}
		if (cur_count &gt; max_count) {
			max_count = cur_count;
			max_char = cur_char;
		}
		prev = cur_char;
	}
	System.out.println(max_count + &quot;&quot; + max_char);
}

