            else if (value.getEstantes()) {
                setBackground(Color.orange);
            } //Tiene solo refrigerado
            else if (value.getRefrigerado()) {
                setBackground(Color.cyan);
            } //no tiene ninguno
            else {
                setBackground(Color.yellow);
            }
            return this;
        }
    }
