            BufferedReader in = new BufferedReader(
                    new InputStreamReader(process.getInputStream()) );
            while ((line = in.readLine()) != null) {
                if(line.toLowerCase().contains(&quot;serial number&quot;)){
                    String[] strings = line.split(&quot; &quot;);
