        int n = array.length;
        int temp = 0;
        for (int i = 0; i &lt; n; i++) {
            for (int j = 1; j &lt; (n); j++) {
                if (array[j - 1] &gt; array[i]) {
                    temp = array[j - 1];
                    array[j - 1] = array[j];
                    array[j] = temp;
