            if (dice == 0 &amp;&amp; scoresSum == target) {
                System.out.println(scores);
                validIterations++;
                return iterations++;
            }
            else if (dice == 0) {
                return iterations++;
            }
            else {
                for (int i = 1; i &lt; sides + 1; i++) {
                    if ((dice - 1 &lt;= (target - scoresSum - i)) &amp;&amp; ((sides * (dice - 1)) &gt;= target - scoresSum - i))
