    &lt;mvc:annotation-driven&gt;
        &lt;mvc:interceptors&gt;
            &lt;ref bean=&quot;interceptor&quot;/&gt;
        &lt;/mvc:interceptors&gt;
    &lt;/mvc:annotation-driven&gt;

