                    int height = 400 - duck.getHeight();

                    int x = rnd.nextInt(width);
                    int y = rnd.nextInt(height);

                    int xDelta = rnd.nextBoolean() ? 1 : -1;
                    int yDelta = rnd.nextBoolean() ? 1 : -1;

                    duck.setLocation(x, y);
