        Map&lt;String, String&gt; foreigns = new HashMap&lt;&gt;();
        foreigns.put(&quot;UK_ob8kqyqqgmefl0aco34akdtpe&quot;, &quot;email&quot;);
        if ( ex.getMessage().contains(&quot;Duplicate entry&quot;) ) {
            Pattern pattern = Pattern.compile(&quot;Duplicate entry &#39;(.*?)&#39; for key&quot;);
            String message = ex.getMessage();
            Matcher matcher = pattern.matcher(message);
            if (matcher.find()) {
                String match = matcher.group(1);
                String key = ((ConstraintViolationException)ex.getCause()).getConstraintName();
                this.setValue(match);
                this.setFieldname(foreigns.get(key));
            }
        }
    }

