class Solution {
    public int findDuplicate(int[] arr) {
        int i =0;
        while(i&lt; arr.length){
            if(arr[i] != i+1){
                if(arr[i] != arr[arr[i]-1]){
                    int temp = arr[i];
                    arr[i] = arr[temp-1];
                    arr[temp-1] = temp;
                }
                else{
                    return arr[i];
                }
            }
            else{
                i++;
            }
            }
         return -1;
        }
    }  //Better Solution

