			} else {
				temp.add(arr.get(right++));
			}
		}

		//for the last right or left remaining element 
		while (left&lt;= mid) {
			temp.add(arr.get(left++));
		}
		while (right&lt;= high) {
			temp.add(arr.get(right++));
		}

		//now we can copy the remaining elements from temp list to arr
		for (int i = low, k = 0; i&lt;= high; i++) {
			arr.set(i, temp.get(k++));
		}
		return total;
	}
}
```


