    public String merge(String leftStr, String rightStr) {	
    		char[]left = leftStr.toCharArray();
    		char[]right = rightStr.toCharArray();
    		int nL = left.length;
    		int nR= right.length;
    		char[] mergeArr= new char[nL+nR];
    		int i=0,j=0,k=0;
    		int temp =0;
    		while(i&lt;nL &amp;&amp; j&lt;nR){
    			
    			if (temp==0){
    				mergeArr[k]=left[i];
    				i++;
    				temp =1;
