```java
static Character nonrepeatingCharacter(String S)
{
    ArrayList&lt;Character&gt; al = new ArrayList&lt;Character&gt;();
    
    for(int i=0; i&lt;S.length(); i++){
        if(!al.contains(S.charAt(i))){
            al.add(S.charAt(i));
        }
        else{
            al.remove(Character.valueOf(S.charAt(i)));
        }
    }
    
    if(al.size() == 0){
        return null;
    }
    
    return al.get(0);
}
