            AtomicInteger tickets = new AtomicInteger(1000);
    
            public void run() {
                while (tickets.get() &gt; 0) {
                    System.out.println(Thread.currentThread().getName()
                            + &quot; is saling&quot; + tickets.decrementAndGet());
