    public abstract class ListDataSource&lt;T extends List&lt;Long&gt;&gt; implements DataSourceList {
    
        private final T list;
        private final Random random;
        private final long DATA = 5;
    
        public ListDataSource(int size) {
            this.list = initializeList();
            this.populateData(size);
            this.random = new Random();
        }
    
        protected void populateData(int size) {
            for (int i = 0; i &lt; size; i++) {
                list.add((long) i);
            }
        }
    
        protected abstract T initializeList();
    
        @Override
        public long addToTheBegin() {
            long start = System.currentTimeMillis();
            list.add(0, DATA);
            long finish = System.currentTimeMillis();
            return finish - start;
        }
    
        @Override
        public long addToMiddle() {
            int middle = list.size() / 2;
            long start = System.currentTimeMillis();
            list.add(middle, DATA);
            long finish = System.currentTimeMillis();
            return finish - start;
        }
    
        @Override
        public long addToEnd() {
            long start = System.currentTimeMillis();
            list.add(DATA);
            long finish = System.currentTimeMillis();
            return finish - start;
        }
    
        @Override
        public long searchValue() {
            long start = System.currentTimeMillis();
            list.get((int)DATA);
            long finish = System.currentTimeMillis();
            return finish - start;
        }
    
        @Override
        public long removeFromBegin() {
            long start = System.currentTimeMillis();
            list.remove(0);
            long finish = System.currentTimeMillis();
            return finish - start;
        }
    
        @Override
        public long removeFromMiddle() {
            int middle = list.size() / 2;
