        private static final int ROWS = 10;
    
        public LayoutEg1() {
            setLayout(new GridBagLayout());
            for (int i = 0; i &lt; ROWS; i++) {
                GridBagConstraints gbc = makeGbc(0, i);
                JLabel label = new JLabel(&quot;Row Label &quot; + (i + 1));
                add(label, gbc);
                
