        list.add(Enums.CCC1);
        list.add(Enums.AAA1);

        System.out.println(list);
        // compare using ordinal
        // list.sort(Comparator.comparing(Enum::ordinal));
        // compare using the enum value name
        list.sort(Comparator.comparing(Enum::name));
        var lowest = list.get(0);
        var highest = list.get(list.size() - 1);
