Simple HashMap Implementation in java

// Implementation class
public class HashMapCustom&lt;K, V&gt;
{
    static class Entry &lt;K,V&gt;{
        K key;
        V value;
        Entry&lt;K,V&gt; next;
        public  Entry(K key, V value,Entry&lt;K,V&gt; next ){
            this.key=key;
            this.value=value;
            this.next=next;
        }
    }

    private Entry&lt;K,V&gt;[] bucket ;
    private int capacity = 4;

    public  HashMapCustom(){
        bucket = new Entry [capacity];
    }

    public void put(K key, V value){
        if(null == key) return; // null keys not allowed
        int hash = hash(key);
        Entry&lt;K,V&gt; add = new Entry&lt;K,V&gt; (key,value,null);
        if(bucket[hash]==null){
            bucket[hash]=add;
        }else{
            Entry&lt;K,V&gt; curr = bucket[hash];
            while(null !=curr){
                if(curr.key.equals (key)){
                    curr.value =value;
                    return;
                }else{
                   if(curr.next == null){
                       curr.next = add;
                       return;
                   }
                   curr = curr.next;
                }
            }
        }
    }
    public V get(K key){
        if(null == key) return null;
        int hash = hash(key);
        Entry&lt;K,V&gt; b = bucket[hash];
        if(null == b) return null;
        while(null !=b){
            if(b.key.equals (key)){
                return b.value;
            }else{
                b=b.next;
            }
        }
        return null;
    }
    public boolean remove(K key){
        if(null == key) return false;
        int hash = hash(key);
        Entry&lt;K,V&gt; b = bucket[hash];
        if(null == b) return false;
        Entry&lt;K,V&gt; prev = null;
        while(null !=b){
            if(b.key.equals (key)){
                //delete
               if(prev == null) {  // first node to remove
                bucket[hash]=b.next;
                return true;
               }else{
                   prev.next = b.next;
                   return true;
               }
            }else{
                prev=b;
                b =b.next;
            }
        }
        return false;
    }
    private int hash (K key)
    {
        return Math.abs (key.hashCode ())%capacity;
    }
}

