        public static int[] frontBackSorted(int[] array) {
            int[] sortedArray = IntStream.of(array).sorted().toArray();
            int n = array.length;
            int[] result = new int[n];
            int i = 0, j = n - 1, k = n - 1;
            boolean front = true;
            while (i &lt;= j) {
                if (front) {
                    result[i++] = sortedArray[k--];
                } else {
                    result[j--] = sortedArray[k--];
                }
                front = !front;
            }
            return result;
        }
    }

