     */
    @Override
    public Class&lt;?&gt; loadClass(String name) throws ClassNotFoundException {
        // Look up and load classes from cache first.
        if (cache.containsKey(name))
            return cache.get(name);

        // If class not cached, load it from given urls in prior.
        String internalName = name.replace(&#39;.&#39;, &#39;/&#39;);
        String path = internalName.concat(&quot;.class&quot;);
        byte[] bytesToLoad = null;
        // Travers given url. Generally only on url.
        for (URL url : getURLs()) {
            FileInputStream fis;
            try {
                fis = new FileInputStream(new File(url.getFile(), path));
            } catch (FileNotFoundException e) {
                // Cannot find class at given urls, continue to next
                continue;
            }
            // Reaching here means we find target class at given urls.
            try {
                bytesToLoad = fis.readAllBytes();
            } catch (IOException e) {
                String msg = String.format(
