            // it is not possible to find another matched url.
            break;
        }

        // Reach here means modified  class has been found at and read properly.
        if (bytesToLoad != null) {
            Class&lt;?&gt; loadedClass;
            try {
                loadedClass = defineClass(name, bytesToLoad, 0, bytesToLoad.length);
            } catch (Throwable e) {
                // Generally LinkageError. Thrown if a class is loaded repeatedly.
                String msg = String.format(
                    &quot;Failed when loading class %s, dispatch to parent&quot;, name);
                throw new ClassNotFoundException(msg, e);
            }
            if (loadedClass != null) {
                // Update cache.
                cache.put(name, loadedClass);
                // Define modified class before dispatching to parent.
                return loadedClass;
            }
        }

        // Dispatch to parent loader.
        return super.loadClass(name);
    }

}
