                      @Override
                      protected void updateItem(Integer item, boolean empty) {
                          if( ! empty){
                              // Your code...
                              setText(Char+val);
                          }else{
                              setText(null); // This will clear the text for empty rows
                          }
                      }
                  };
               }
            });

