import static com.mojang.blaze3d.platform.GlConst.GL_FRAGMENT_SHADER;
import static com.mojang.blaze3d.platform.GlConst.GL_VERTEX_SHADER;


public final class SimplifiedFontRenderer implements Wrapper {

	public static float drawString(PoseStack matrices, TextFont font, String text, double x, double y, Color color) {
		return renderString(matrices, font, text, x, y, color);
	}
	
	public static float drawCenteredXString(PoseStack matrices, TextFont font, String text, double x, double y, Color color) {
		return renderString(matrices, font, text, x - font.getWidth(text) / 2.0f, y, color);
	}

	public static float drawCenteredYString(PoseStack matrices, TextFont font, String text, double x, double y, Color color) {
		return renderString(matrices, font, text, x, y + font.getLifting() / 2.0f + 0.5f, color);
	}
	
	public static float drawCenteredXYString(PoseStack matrices, TextFont font, String text, double x, double y, Color color) {
		return renderString(matrices, font, text, x - font.getWidth(text) / 2.0f, y + font.getLifting() / 2.0f + 0.5f, color);
	}
	
	public static float drawShadowedString(PoseStack matrices, TextFont font, String text, double x, double y, Color color) {
		return renderStringWithShadow(matrices, font, text, x, y, color, getShadowColor(color));
	}
	
	public static float drawShadowedCenteredXString(PoseStack matrices, TextFont font, String text, double x, double y, Color color) {
		return renderStringWithShadow(matrices, font, text, x - font.getWidth(text) / 2.0f, y, color, getShadowColor(color));
	}
	
	public static float drawShadowedCenteredYString(PoseStack matrices, TextFont font, String text, double x, double y, Color color) {
		return renderStringWithShadow(matrices, font, text, x, y + font.getLifting() / 2.0f + 0.5f, color, getShadowColor(color));
	}
	
	public static float drawShadowedCenteredXYString(PoseStack matrices, TextFont font, String text, double x, double y, Color color) {
		return renderStringWithShadow(matrices, font, text, x - font.getWidth(text) / 2.0f, y + font.getLifting() / 2.0f + 0.5f, color, getShadowColor(color));
	}
	
	public static float drawShadowedString(PoseStack matrices, TextFont font, String text, double x, double y, Color color, Color shadowColor) {
		return renderStringWithShadow(matrices, font, text, x, y, color, shadowColor);
	}
	
	public static float drawShadowedCenteredXString(PoseStack matrices, TextFont font, String text, double x, double y, Color color, Color shadowColor) {
		return renderStringWithShadow(matrices, font, text, x - font.getWidth(text) / 2.0f, y, color, shadowColor);
	}
	
	public static float drawShadowedCenteredYString(PoseStack matrices, TextFont font, String text, double x, double y, Color color, Color shadowColor) {
		return renderStringWithShadow(matrices, font, text, x, y + font.getLifting() / 2.0f + 0.5f, color, shadowColor);
	}
	
	public static float drawShadowedCenteredXYString(PoseStack matrices, TextFont font, String text, double x, double y, Color color, Color shadowColor) {
		return renderStringWithShadow(matrices, font, text, x - font.getWidth(text) / 2.0f, y + font.getLifting() / 2.0f + 0.5f, color, shadowColor);
	}
	
	private static float renderStringWithShadow(PoseStack matrices, TextFont font, String text, double x, double y, Color color, Color shadowColor) {
		renderString(matrices, font, text, x + 1.0F, y, shadowColor);
		return renderString(matrices, font, text, x, y -= 1.0f, color) + 1.0f;
	}

	// returns string width

	private static float renderString(PoseStack matrices, TextFont font, String text, double x, double y, Color color) {
		y -= font.getLifting();
		int color1 = color.getRGB();
		float alpha = (float) FastColor.ARGB32.alpha(color1) / 255.0F;
		float red = (float)FastColor.ARGB32.red(color1) / 255.0F;
		float green = (float)FastColor.ARGB32.green(color1) / 255.0F;
		float blue = (float)FastColor.ARGB32.blue(color1) / 255.0F;

		float startPos = (float) x * 2f;
		float posX = startPos;
		float posY = (float) y * 2f;

		

		matrices.pushPose();
		
		//RenderSystem.enableBlend();




		GL30.glEnable(GL11.GL_BLEND);
		GlStateManager._blendFuncSeparate(770, 771, 1, 0);
		GlStateManager._blendFunc(770, 771);


		//RenderSystem.enableColorLogicOp();
		//RenderSystem.enableCull();
		//RenderSystem.enableDepthTest();

		matrices.scale(0.5f, 0.5f, 1f);

		Matrix4f matrix = matrices.last().pose();

		font.bindTex();

		for (int i = 0; i &lt; text.length(); i++) {
			posX += font.renderGlyph(matrix, text.charATextFontt(i), posX, posY, red, green, blue, alpha);
		}
		
		font.unbindTex();

		matrices.popPose();
		RenderSystem.disableBlend();
		RenderSystem.disableDepthTest();
		//RenderSystem.disableColorLogicOp();
		RenderSystem.disableCull();
		GL30.glDisable(GL11.GL_BLEND);
		return (posX - startPos) / 2.0f;
	}
	
	public static Color getShadowColor(Color color) {
		return new Color((color.getRGB() &amp; 16579836) &gt;&gt; 2 | color.getRGB()  &amp; -16777216);
	}

}

