      strcpy(temp_arr, codes[start]);
    
      while (j &lt; n &amp;&amp; arr[j] &lt; key) {
        arr[j - 1] = arr[j];
        /* codes should also move according to map values */
        strcpy(codes[j - 1 + start], codes[j + start]);
        j = j + 1;
      }
      arr[j - 1] = key;
      strcpy(codes[j - 1 + start], temp_arr);
    }


