    fun localFileExist(localPathOrUri: String?, context:Context): Boolean {
        if (localPathOrUri.isNullOrEmpty()) {
            return false
        }

        var exists = File(localPathOrUri).exists()
        if (exists) {
            return exists
        }

        val cR = context.getContentResolver()
