      &lt;version&gt;20220320&lt;/version&gt;
    &lt;/dependency&gt;
    &lt;dependency&gt;
      &lt;groupId&gt;jakarta.ws.rs&lt;/groupId&gt;
      &lt;artifactId&gt;jakarta.ws.rs-api&lt;/artifactId&gt;
      &lt;version&gt;2.1.6&lt;/version&gt;
    &lt;/dependency&gt;
