        List result = new ArrayList&lt;&gt;();
        List current = nestedList;
        while (current.get(0) instanceof List&lt;?&gt;) {
            for (Object next: current) {
                result.addAll((List) next);
            }
            current = result;
            result = new ArrayList&lt;&gt;();
