						output[outputPos++] = &#39;\&#39;&#39;;
			            break;
					case &#39;\u0192&#39;:  //Æ’ 0x83
						output[outputPos++] = &#39;f&#39;;
			            break;
					case &#39;\u201e&#39;:  //â€ž 0x84
						output[outputPos++] = &#39;&quot;&#39;;
			            break;
					case &#39;\u2026&#39;:  //â€¦ 0x85
						output[outputPos++] = &#39;.&#39;;
