			            break;
					case &#39;\u2030&#39;:  //â€° 0x89
						output[outputPos++] = &#39;p&#39;;
						output[outputPos++] = &#39;e&#39;;
						output[outputPos++] = &#39;r&#39;;
						output[outputPos++] = &#39;m&#39;;
						output[outputPos++] = &#39;i&#39;;
						output[outputPos++] = &#39;l&#39;;

			            break;
					case &#39;\u0160&#39;:  //Å  0x8a
						output[outputPos++] = &#39;S&#39;;
			            break;
					case &#39;\u2039&#39;:  //â€¹ 0x8b
						output[outputPos++] = &#39;\&#39;&#39;;
			            break;
					case &#39;\u0152&#39;:  //Å’ 0x8c
						output[outputPos++] = &#39;O&#39;;
