						output[outputPos++] = &#39;Z&#39;;
			            break;
					case &#39;\u2018&#39;:  //â€˜ 0x91
						output[outputPos++] = &#39;\&#39;&#39;;
			            break;
					case &#39;\u2019&#39;:  //â€™ 0x92
						output[outputPos++] = &#39;\&#39;&#39;;
			            break;
					case &#39;\u201c&#39;:  //â€œ 0x93
						output[outputPos++] = &#39;&quot;&#39;;
			            break;
					case &#39;\u201d&#39;:  //â€ 0x94
						output[outputPos++] = &#39;&quot;&#39;;
			            break;
