						output[outputPos++] = &#39;-&#39;;
			            break;
					case &#39;\u2014&#39;:  //â€” 0x97
						output[outputPos++] = &#39;-&#39;;
			            break;
					case &#39;\u02dc&#39;:  //Ëœ 0x98
						output[outputPos++] = &#39;~&#39;;
			            break;
					case &#39;\u2122&#39;:  //â„¢ 0x99
						output[outputPos++] = &#39;(&#39;;
						output[outputPos++] = &#39;T&#39;;
						output[outputPos++] = &#39;M&#39;;
						output[outputPos++] = &#39;)&#39;;
			            break;
					case &#39;\u0161&#39;:  //Å¡ 0x9a
						output[outputPos++] = &#39;s&#39;;
			            break;
					case &#39;\u203a&#39;:  //â€º 0x9b
						output[outputPos++] = &#39;\&#39;&#39;;
			            break;
					case &#39;\u0153&#39;:  //Å“ 0x9c
