		return countH(first, i);
	}
	
	private int countH(Node front, int i) {  // TODO
		if(front==null) {
			return 0;
		}
		if (front.item == i) {
		    return 1 + countH(front.next, i);
		} else {
		    return countH(front.next, i);
		}
	}
```

