      static Specification&lt;User&gt; search(User entity) {
        return (root, cq, cb) -&gt; {
          //To ensure we start with a predicate
          Predicate predicate = cb.isTrue(cb.literal(true));
          if(entity.getFirstName() != null &amp;&amp; !entity.getFirstName().isBlank()) {
