			System.out.println(&quot;Pausing execution for &quot; + duration + &quot; ms...&quot;); 
		    TimeUnit.MILLISECONDS.sleep(duration);
		
			System.out.println(&quot;Bot Command: PRIVMSG &quot; + Channel + Message); //diagnostic
