        public static int ubound(LinkedList&lt;Integer&gt; ln, int x) {
            int l = 0;
            int h = ln.size();
            while (l &lt; h) {
                int mid = (l + h) / 2;
                if (ln.get(mid) &lt;= x) l = mid + 1;
                else h = mid;
            }
            return l;
        }

        public void solve() 
