This can be solved in O(n) time and O(n) space. Idea is to find all subarrays such that A[i] is minimum element.

    private static final int M = (int) 1e9 + 7;

    public static int solve(int[] A) {
        int n = A.length;
        long[] ps = prefixOfPrefixSum(A);
        int[] left = leftSmaller(A);
        int[] right = rightSmaller(A);
        long res = 0;
        for (int i = 0; i &lt; n; i++) {
            int l = left[i];
            int r = right[i];
            long t1 = (i - l) * (ps[r] - ps[i]) % M + M;
            long t2 = (r - i) * (ps[i] - ps[Math.max(l, 0)]) % M;
            res += ((t1 - t2) * A[i]) % M;
            res %= M;
        }
        return (int) res;
    }

    private static int[] leftSmaller(int[] A) {
        int n = A.length;
        int[] res = new int[n];
        Stack&lt;Integer&gt; st = new Stack&lt;&gt;();
        for (int i = 0; i &lt; n; i++) {
            while (!st.isEmpty() &amp;&amp; A[st.peek()] &gt;= A[i]) {
                st.pop();
            }
            res[i] = st.isEmpty() ? -1 : st.peek();
            st.push(i);
        }
        return res;
    }

    private static int[] rightSmaller(int[] A) {
        int n = A.length;
        int[] res = new int[n];
        Stack&lt;Integer&gt; st = new Stack&lt;&gt;();
        for (int i = n - 1; i &gt;= 0; i--) {
            while (!st.isEmpty() &amp;&amp; A[st.peek()] &gt; A[i]) {
                st.pop();
            }
            res[i] = st.isEmpty() ? n : st.peek();
            st.push(i);
        }
        return res;
    }

    private static long[] prefixOfPrefixSum(int[] A) {
        int n = A.length;
        long[] temp = new long[n + 1];
        long[] res = new long[n + 1];
        for (int i = 1; i &lt;= n; i++) {
            temp[i] = (temp[i - 1] + A[i - 1]) % M;
            temp[i] %= M;
        }
        for (int i = 1; i &lt;= n; i++) {
            res[i] = (res[i - 1] + temp[i]) % M;
            res[i] %= M;
        }
        return res;
    }

